@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 0 0% 100%;
    --foreground: 222.2 84% 4.9%;
    --card: 0 0% 100%;
    --card-foreground: 222.2 84% 4.9%;
    --popover: 0 0% 100%;
    --popover-foreground: 222.2 84% 4.9%;
    --primary: 45 100% 50%;
    --primary-foreground: 0 0% 0%;
    --secondary: 45 100% 95%;
    --secondary-foreground: 0 0% 20%;
    --muted: 45 50% 90%;
    --muted-foreground: 0 0% 45%;
    --accent: 45 100% 90%;
    --accent-foreground: 0 0% 20%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 210 40% 98%;
    --border: 214.3 31.8% 91.4%;
    --input: 214.3 31.8% 91.4%;
    --ring: 221.2 83.2% 53.3%;
    --radius: 0.5rem;

    /* Glass morphism variables */
    --glass-bg: 0 0% 100% / 0.8;
    --glass-border: 0 0% 100% / 0.2;
    --glass-shadow: 0 0 20px rgba(0, 0, 0, 0.1);

    /* Gradient variables */
    --gradient-primary: linear-gradient(135deg, hsl(45 100% 50%) 0%, hsl(35 100% 60%) 100%);
    --gradient-secondary: linear-gradient(135deg, hsl(45 100% 95%) 0%, hsl(45 100% 90%) 100%);
    --gradient-surface: linear-gradient(135deg, hsl(0 0% 100%) 0%, hsl(45 100% 95%) 100%);
    --gradient-hero: linear-gradient(135deg, hsl(45 100% 50%) 0%, hsl(35 100% 60%) 50%, hsl(25 100% 70%) 100%);

    /* Shadow variables */
    --shadow-glass: 0 8px 32px rgba(31, 38, 135, 0.37);
    --shadow-elevation: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
    --shadow-glow: 0 0 20px rgba(59, 130, 246, 0.3);
  }

  .dark {
    --background: 222.2 84% 4.9%;
    --foreground: 210 40% 98%;
    --card: 222.2 84% 4.9%;
    --card-foreground: 210 40% 98%;
    --popover: 222.2 84% 4.9%;
    --popover-foreground: 210 40% 98%;
    --primary: 217.2 91.2% 59.8%;
    --primary-foreground: 222.2 84% 4.9%;
    --secondary: 217.2 32.6% 17.5%;
    --secondary-foreground: 210 40% 98%;
    --muted: 217.2 32.6% 17.5%;
    --muted-foreground: 215 20.2% 65.1%;
    --accent: 217.2 32.6% 17.5%;
    --accent-foreground: 210 40% 98%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 40% 98%;
    --border: 217.2 32.6% 17.5%;
    --input: 217.2 32.6% 17.5%;
    --ring: 224.3 76.3% 94.1%;

    /* Glass morphism variables for dark mode */
    --glass-bg: 222.2 84% 4.9% / 0.8;
    --glass-border: 217.2 32.6% 17.5% / 0.2;
    --glass-shadow: 0 0 20px rgba(0, 0, 0, 0.3);

    /* Shadow variables for dark mode */
    --shadow-glass: 0 8px 32px rgba(0, 0, 0, 0.5);
    --shadow-elevation: 0 4px 6px -1px rgba(0, 0, 0, 0.3), 0 2px 4px -1px rgba(0, 0, 0, 0.2);
    --shadow-glow: 0 0 20px rgba(59, 130, 246, 0.5);
  }
}

@layer components {
  .glass {
    background: hsl(var(--glass-bg));
    backdrop-filter: blur(10px);
    -webkit-backdrop-filter: blur(10px);
    border: 1px solid hsl(var(--glass-border));
    box-shadow: var(--shadow-glass);
  }

  .card {
    background: hsl(var(--card));
    border: 1px solid hsl(var(--border));
    border-radius: var(--radius);
    box-shadow: var(--shadow-elevation);
  }

  .btn-primary {
    background: hsl(var(--primary));
    color: hsl(var(--primary-foreground));
    border-radius: var(--radius);
    padding: 0.5rem 1rem;
    font-weight: 500;
    transition: all 0.2s ease-in-out;
  }

  .btn-primary:hover {
    background: hsl(var(--primary) / 0.9);
  }

  .btn-secondary {
    background: hsl(var(--secondary));
    color: hsl(var(--secondary-foreground));
    border: 1px solid hsl(var(--border));
    border-radius: var(--radius);
    padding: 0.5rem 1rem;
    font-weight: 500;
    transition: all 0.2s ease-in-out;
  }

  .btn-secondary:hover {
    background: hsl(var(--accent));
  }
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }
}

src/components/GoogleMap.tsx.content:
import React, { useEffect, useRef, useState } from 'react';
import { View, Text, TouchableOpacity, Alert, StyleSheet } from 'react-native';
import MapView, { Marker, Polyline, PROVIDER_GOOGLE } from 'react-native-maps';
import * as Location from 'expo-location';
import { GOOGLE_MAPS_API_KEY } from '@/constants';

interface LocationData {
  lat: number;
  lng: number;
  address: string;
}

interface RouteData {
  distance: string;
  duration: string;
  distanceKm: number;
  durationMinutes: number;
  polyline?: string;
}

interface GoogleMapProps {
  pickupLocation?: LocationData | null;
  dropoffLocation?: LocationData | null;
  height?: string | number;
  onRouteUpdate?: (routeData: RouteData) => void;
  interactive?: boolean;
  onPickupChange?: (location: LocationData) => void;
  onDropoffChange?: (location: LocationData) => void;
  activeMarker?: 'pickup' | 'dropoff';
}

export const GoogleMap: React.FC<GoogleMapProps> = ({
  pickupLocation,
  dropoffLocation,
  height = '300px',
  onRouteUpdate,
  interactive = false,
  onPickupChange,
  onDropoffChange,
  activeMarker = 'pickup',
}) => {
  const mapRef = useRef<MapView>(null);
  const [routeCoordinates, setRouteCoordinates] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentLocation, setCurrentLocation] = useState<LocationData | null>(null);
  const [region, setRegion] = useState<any>(null);
  const [isLocationLoading, setIsLocationLoading] = useState(true);

  // Get current location
  const getCurrentLocation = async () => {
    setIsLocationLoading(true);
    try {
      console.log('Requesting location permissions...');
      const { status } = await Location.requestForegroundPermissionsAsync();
      console.log('Location permission status:', status);

      if (status === 'granted') {
        console.log('Getting current position...');
        const location = await Location.getCurrentPositionAsync({
          accuracy: Location.Accuracy.High,
        });

        console.log('Current location:', location.coords);

        const locationData: LocationData = {
          lat: location.coords.latitude,
          lng: location.coords.longitude,
          address: 'Current Location',
        };

        setCurrentLocation(locationData);

        // Update region to show current location
        const newRegion = {
          latitude: location.coords.latitude,
          longitude: location.coords.longitude,
          latitudeDelta: 0.01,
          longitudeDelta: 0.01,
        };
        setRegion(newRegion);
        console.log('Set region to current location:', newRegion);
      } else {
        console.log('Location permission denied');
        // Fallback to Bangalore coordinates if permission denied
        const fallbackRegion = {
          latitude: 12.9716,
          longitude: 77.5946,
          latitudeDelta: 0.1,
          longitudeDelta: 0.1,
        };
        setRegion(fallbackRegion);
      }
    } catch (error) {
      console.error('Error getting current location:', error);
      // Fallback to Bangalore coordinates if location fails
      const fallbackRegion = {
        latitude: 12.9716,
        longitude: 77.5946,
        latitudeDelta: 0.1,
        longitudeDelta: 0.1,
      };
      setRegion(fallbackRegion);
    } finally {
      setIsLocationLoading(false);
    }
  };

  // Get current location on component mount
  useEffect(() => {
    getCurrentLocation();
  }, []);

  // Calculate route when both locations are available
  useEffect(() => {
    if (pickupLocation && dropoffLocation) {
      calculateRoute();
    } else {
      setRouteCoordinates([]);
    }
  }, [pickupLocation, dropoffLocation]);

  // Update region when locations change
  useEffect(() => {
    console.log('Locations changed:', { pickupLocation, dropoffLocation });

    if (pickupLocation || dropoffLocation) {
      const coordinates: any[] = [];

      if (pickupLocation) {
        console.log('Adding pickup coordinate:', pickupLocation);
        coordinates.push({
          latitude: pickupLocation.lat,
          longitude: pickupLocation.lng,
        });
      }

      if (dropoffLocation) {
        console.log('Adding dropoff coordinate:', dropoffLocation);
        coordinates.push({
          latitude: dropoffLocation.lat,
          longitude: dropoffLocation.lng,
        });
      }

      if (coordinates.length > 0) {
        // Calculate center point
        const avgLat = coordinates.reduce((sum, coord) => sum + coord.latitude, 0) / coordinates.length;
        const avgLng = coordinates.reduce((sum, coord) => sum + coord.longitude, 0) / coordinates.length;

        // Calculate appropriate zoom level based on distance
        let latitudeDelta = 0.01;
        let longitudeDelta = 0.01;

        if (coordinates.length > 1) {
          const latDiff = Math.abs(coordinates[0].latitude - coordinates[1].latitude);
          const lngDiff = Math.abs(coordinates[0].longitude - coordinates[1].longitude);
          latitudeDelta = Math.max(latDiff * 1.5, 0.01);
          longitudeDelta = Math.max(lngDiff * 1.5, 0.01);
        }

        const newRegion = {
          latitude: avgLat,
          longitude: avgLng,
          latitudeDelta,
          longitudeDelta,
        };

        console.log('Setting new region:', newRegion);
        setRegion(newRegion);

        // Also fit to coordinates if map is ready
        if (mapRef.current) {
          mapRef.current.fitToCoordinates(coordinates, {
            edgePadding: { top: 50, right: 50, bottom: 50, left: 50 },
            animated: true,
          });
        }
      }
    }
  }, [pickupLocation, dropoffLocation]);

  const calculateRoute = async () => {
    if (!pickupLocation || !dropoffLocation) return;

    setIsLoading(true);
    try {
      const origin = `${pickupLocation.lat},${pickupLocation.lng}`;
      const destination = `${dropoffLocation.lat},${dropoffLocation.lng}`;

      const response = await fetch(
        `https://maps.googleapis.com/maps/api/directions/json?origin=${origin}&destination=${destination}&key=${GOOGLE_MAPS_API_KEY}&mode=driving&units=metric`
      );

      const data = await response.json();

      if (data.status === 'OK' && data.routes.length > 0) {
        const route = data.routes[0];
        const leg = route.legs[0];

        // Decode polyline
        const points = decodePolyline(route.overview_polyline.points);
        setRouteCoordinates(points);

        // Extract route data
        const routeData: RouteData = {
          distance: leg.distance.text,
          duration: leg.duration.text,
          distanceKm: leg.distance.value / 1000, // Convert meters to km
          durationMinutes: Math.round(leg.duration.value / 60), // Convert seconds to minutes
          polyline: route.overview_polyline.points,
        };

        onRouteUpdate?.(routeData);
      } else if (data.status === 'ZERO_RESULTS') {
        console.error('No route found between locations');
        Alert.alert('No Route Found', 'Unable to find a route between these locations. Please try different locations.');
        setRouteCoordinates([]);
      } else if (data.status === 'OVER_QUERY_LIMIT') {
        console.error('Directions API quota exceeded');
        Alert.alert('Service Unavailable', 'Route calculation service is temporarily unavailable. Please try again later.');
        setRouteCoordinates([]);
      } else if (data.status === 'REQUEST_DENIED') {
        console.error('Directions API request denied');
        Alert.alert('Service Error', 'Route calculation service is currently unavailable.');
        setRouteCoordinates([]);
      } else {
        console.error('Directions API error:', data.status);
        Alert.alert('Route Error', 'Unable to calculate route. Please check your locations.');
        setRouteCoordinates([]);
      }
    } catch (error) {
      console.error('Route calculation error:', error);
      Alert.alert('Error', 'Failed to calculate route. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  // Decode Google Maps polyline
  const decodePolyline = (encoded: string) => {
    const points: any[] = [];
    let index = 0;
    let lat = 0;
    let lng = 0;

    while (index < encoded.length) {
      let shift = 0;
      let result = 0;
      let byte: number;

      do {
        byte = encoded.charCodeAt(index++) - 63;
        result |= (byte & 0x1f) << shift;
        shift += 5;
      } while (byte >= 0x20);

      const dlat = result & 1 ? ~(result >> 1) : result >> 1;
      lat += dlat;

      shift = 0;
      result = 0;

      do {
        byte = encoded.charCodeAt(index++) - 63;
        result |= (byte & 0x1f) << shift;
        shift += 5;
      } while (byte >= 0x20);

      const dlng = result & 1 ? ~(result >> 1) : result >> 1;
      lng += dlng;

      points.push({
        latitude: lat / 1e5,
        longitude: lng / 1e5,
      });
    }

    return points;
  };

  const handleMapPress = async (event: any) => {
    if (!interactive) return;

    const { coordinate } = event.nativeEvent;
    const location: LocationData = {
      lat: coordinate.latitude,
      lng: coordinate.longitude,
      address: `${coordinate.latitude.toFixed(6)}, ${coordinate.longitude.toFixed(6)}`,
    };

    try {
      // Reverse geocode to get address
      const response = await fetch(
        `https://maps.googleapis.com/maps/api/geocode/json?latlng=${coordinate.latitude},${coordinate.longitude}&key=${GOOGLE_MAPS_API_KEY}`
      );

      const data = await response.json();
      if (data.status === 'OK' && data.results.length > 0) {
        location.address = data.results[0].formatted_address;
      }
    } catch (error) {
      console.error('Reverse geocoding error:', error);
    }

    if (activeMarker === 'pickup' && onPickupChange) {
      onPickupChange(location);
    } else if (activeMarker === 'dropoff' && onDropoffChange) {
      onDropoffChange(location);
    }
  };

  const getMapRegion = () => {
    // If we have a region set (from current location), use it
    if (region) {
      return region;
    }

    // If pickup location exists, center on it
    if (pickupLocation) {
      return {
        latitude: pickupLocation.lat,
        longitude: pickupLocation.lng,
        latitudeDelta: 0.01,
        longitudeDelta: 0.01,
      };
    }

    // If dropoff location exists, center on it
    if (dropoffLocation) {
      return {
        latitude: dropoffLocation.lat,
        longitude: dropoffLocation.lng,
        latitudeDelta: 0.01,
        longitudeDelta: 0.01,
      };
    }

    // Default to Bangalore (more central location)
    return {
      latitude: 12.9716,
      longitude: 77.5946,
      latitudeDelta: 0.1,
      longitudeDelta: 0.1,
    };
  };

  return (
    <View style={styles.container}>
      <MapView
        ref={mapRef}
        provider={PROVIDER_GOOGLE}
        style={{
          width: '100%',
          height: typeof height === 'string' ? parseInt(height) : height,
        }}
        region={region || getMapRegion()}
        onPress={handleMapPress}
        showsUserLocation={true}
        showsMyLocationButton={interactive}
        zoomEnabled={interactive}
        scrollEnabled={interactive}
        rotateEnabled={interactive}
        loadingEnabled={true}
        loadingIndicatorColor="#2563eb"
        loadingBackgroundColor="#ffffff"
        onMapReady={() => {
          console.log('Map is ready');
        }}
        onRegionChangeComplete={(newRegion) => {
          // Update region state when user interacts with map
          if (interactive) {
            setRegion(newRegion);
          }
        }}
      >
        {/* Pickup Marker */}
        {pickupLocation && pickupLocation.lat && pickupLocation.lng && (
          <Marker
            coordinate={{
              latitude: pickupLocation.lat,
              longitude: pickupLocation.lng,
            }}
            title="Pickup Location"
            description={pickupLocation.address || 'Pickup Location'}
            pinColor="green"
          />
        )}

        {/* Dropoff Marker */}
        {dropoffLocation && dropoffLocation.lat && dropoffLocation.lng && (
          <Marker
            coordinate={{
              latitude: dropoffLocation.lat,
              longitude: dropoffLocation.lng,
            }}
            title="Drop-off Location"
            description={dropoffLocation.address || 'Drop-off Location'}
            pinColor="red"
          />
        )}

        {/* Current Location Marker */}
        {currentLocation && (
          <Marker
            coordinate={{
              latitude: currentLocation.lat,
              longitude: currentLocation.lng,
            }}
            title="Current Location"
            description="Your current location"
            pinColor="blue"
          />
        )}

        {/* Route Polyline */}
        {routeCoordinates.length > 0 && (
          <Polyline
            coordinates={routeCoordinates}
            strokeColor="#2563eb"
            strokeWidth={4}
          />
        )}
      </MapView>

      {/* Loading Indicator */}
      {(isLoading || isLocationLoading) && (
        <View style={styles.loadingOverlay}>
          <View style={styles.loadingContainer}>
            <Text style={styles.loadingText}>
              {isLocationLoading ? 'Getting your location...' : 'Calculating route...'}
            </Text>
          </View>
        </View>
      )}

      {/* Interactive Mode Indicator */}
      {interactive && (
        <View style={styles.interactiveIndicator}>
          <Text style={styles.interactiveText}>
            Tap map to set {activeMarker === 'pickup' ? 'pickup' : 'drop-off'} location
          </Text>
        </View>
      )}
    </View>
  );
