import React, { useState } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  Modal,
  ScrollView,
  Alert,
  StyleSheet,
} from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';
import { useAppStore } from '../stores/appStore';

export default function NotificationBell() {
  const { notifications, markNotificationAsRead } = useAppStore();
  const [showNotifications, setShowNotifications] = useState(false);

  const unreadCount = notifications.filter(n => !n.is_read).length;

  const handleNotificationPress = (notificationId: string) => {
    markNotificationAsRead(notificationId);
  };

  const handleClearAll = () => {
    Alert.alert(
      'Clear All Notifications',
      'Are you sure you want to mark all notifications as read?',
      [
        { text: 'Cancel', style: 'cancel' },
        {
          text: 'Clear All',
          onPress: () => {
            notifications.forEach(notification => {
              if (!notification.is_read) {
                markNotificationAsRead(notification.id);
              }
            });
          }
        },
      ]
    );
  };

  return (
    <>
      <TouchableOpacity
        style={styles.bellContainer}
        onPress={() => setShowNotifications(true)}
      >
        <MaterialIcons name="notifications" size={20} color="#64748b" />
        {unreadCount > 0 && (
          <View style={styles.badge}>
            <Text style={styles.badgeText}>
              {unreadCount > 99 ? '99+' : unreadCount}
            </Text>
          </View>
        )}
      </TouchableOpacity>

      <Modal
        visible={showNotifications}
        transparent
        animationType="slide"
        onRequestClose={() => setShowNotifications(false)}
      >
        <View style={styles.modalOverlay}>
          <View style={styles.modalContent}>
            <View style={styles.modalHeader}>
              <Text style={styles.modalTitle}>
                Notifications ({notifications.length})
              </Text>
              <View style={styles.modalActions}>
                {unreadCount > 0 && (
                  <TouchableOpacity
                    style={styles.clearButton}
                    onPress={handleClearAll}
                  >
                    <Text style={styles.clearButtonText}>Clear All</Text>
                  </TouchableOpacity>
                )}
                <TouchableOpacity
                  style={styles.closeButton}
                  onPress={() => setShowNotifications(false)}
                >
                  <MaterialIcons name="close" size={18} color="#64748b" />
                </TouchableOpacity>
              </View>
            </View>

            <ScrollView style={styles.scrollContainer}>
              {notifications.length === 0 ? (
                <View style={styles.emptyContainer}>
                  <View style={styles.emptyIcon}>
                    <MaterialIcons name="notifications" size={48} color="#cbd5e1" />
                  </View>
                  <Text style={styles.emptyTitle}>
                    No notifications yet
                  </Text>
                  <Text style={styles.emptySubtitle}>
                    You'll receive updates about your bookings here
                  </Text>
                </View>
              ) : (
                notifications.map((notification) => (
                  <TouchableOpacity
                    key={notification.id}
                    style={[
                      styles.notificationItem,
                      !notification.is_read && styles.notificationItemUnread
                    ]}
                    onPress={() => handleNotificationPress(notification.id)}
                  >
                    <View style={styles.notificationLeft}>
                      <View style={[
                        styles.notificationDot,
                        {
                          backgroundColor:
                            notification.type === 'booking' ? '#2563eb' :
                            notification.type === 'payment' ? '#16a34a' :
                            notification.type === 'system' ? '#f59e0b' :
                            '#a855f7'
                        }
                      ]} />
                      <View style={styles.notificationContent}>
                        <Text style={[
                          styles.notificationTitle,
                          !notification.is_read && styles.notificationTitleUnread
                        ]}>
                          {notification.title}
                        </Text>
                        <Text style={[
                          styles.notificationMessage,
                          !notification.is_read && styles.notificationMessageUnread
                        ]}>
                          {notification.message}
                        </Text>
                        <Text style={styles.notificationTime}>
                          {new Date(notification.created_at).toLocaleString()}
                        </Text>
                      </View>
                      {!notification.is_read && (
                        <View style={styles.unreadIndicator} />
                      )}
                    </View>
                  </TouchableOpacity>
                ))
              )}
            </ScrollView>
          </View>
        </View>
      </Modal>
    </>
  );
}

const styles = StyleSheet.create({
