src/hooks/useFareCalculation.ts.content:
import { useMemo } from 'react';
import { ServiceType, VehicleType } from '@/types';
import { FARE_CONFIG } from '@/constants';

interface FareCalculationParams {
  serviceType: ServiceType;
  vehicleType: VehicleType;
  distanceKm: number;
  durationMinutes: number;
  scheduledDateTime?: string;
  currentTime?: Date;
}

interface FareData {
  baseFare: number;
  distanceFare: number;
  timeFare: number;
  surgeMultiplier: number;
  totalFare: number;
  estimatedDuration: number;
  estimatedDistance: number;
  surgeReason?: string;
}

export const useFareCalculation = ({
  serviceType,
  vehicleType,
  distanceKm,
  durationMinutes,
  scheduledDateTime,
  currentTime = new Date(),
}: FareCalculationParams): FareData | null => {
  return useMemo(() => {
    if (!distanceKm || !durationMinutes) return null;

    // Get base rates from config
    const baseFare = FARE_CONFIG.BASE_FARE[serviceType] || 50;
    const perKmRate = FARE_CONFIG.PER_KM_RATE[serviceType] || 12;
    const perMinuteRate = FARE_CONFIG.PER_MINUTE_RATE[serviceType] || 2;

    // Calculate fares
    const distanceFare = distanceKm * perKmRate;
    const timeFare = durationMinutes * perMinuteRate;

    // Apply vehicle type multiplier
    let vehicleMultiplier = 1;
    switch (vehicleType) {
      case 'suv':
        vehicleMultiplier = 1.3;
        break;
      case 'premium':
        vehicleMultiplier = 1.8;
        break;
      case 'sedan':
      default:
        vehicleMultiplier = 1;
        break;
    }

    // Calculate surge multiplier based on time and demand
    let surgeMultiplier = 1.0;
    let surgeReason = '';

    // Time-based surge pricing
    const hour = currentTime.getHours();
    const day = currentTime.getDay();

    // Peak hours (7-9 AM, 5-7 PM on weekdays)
    if (day >= 1 && day <= 5) { // Monday to Friday
      if ((hour >= 7 && hour <= 9) || (hour >= 17 && hour <= 19)) {
        surgeMultiplier = 1.5;
        surgeReason = 'Peak hours';
      }
    }

    // Weekend surge (Friday 8 PM to Sunday 10 PM)
    if (day === 5 && hour >= 20) { // Friday evening
      surgeMultiplier = 1.3;
      surgeReason = 'Weekend start';
    } else if (day === 6 || (day === 0 && hour <= 22)) { // Saturday or Sunday before 10 PM
      surgeMultiplier = 1.4;
      surgeReason = 'Weekend';
    }

    // Special events surge (can be enhanced with real event data)
    // For now, using simplified logic
    if (serviceType === 'airport') {
      // Airport services have slight surge during peak travel times
      if ((hour >= 4 && hour <= 7) || (hour >= 18 && hour <= 21)) {
        surgeMultiplier = Math.max(surgeMultiplier, 1.2);
        surgeReason = surgeReason ? `${surgeReason} + Airport peak` : 'Airport peak';
      }
    }

    // Distance-based surge for very short rides (under 2km)
    if (distanceKm < 2) {
      surgeMultiplier *= 1.1;
      surgeReason = surgeReason ? `${surgeReason} + Short trip` : 'Short trip';
    }

    // Long distance surge for rides over 50km
    if (distanceKm > 50) {
      surgeMultiplier *= 1.2;
      surgeReason = surgeReason ? `${surgeReason} + Long distance` : 'Long distance';
    }

    // Scheduled booking discount (opposite of surge)
    if (scheduledDateTime) {
      const scheduledTime = new Date(scheduledDateTime);
      const hoursDifference = (scheduledTime.getTime() - currentTime.getTime()) / (1000 * 60 * 60);

      if (hoursDifference > 24) { // Booked more than 24 hours in advance
        surgeMultiplier *= 0.9;
        surgeReason = surgeReason ? `${surgeReason} + Advance booking discount` : 'Advance booking discount';
      } else if (hoursDifference > 4) { // Booked more than 4 hours in advance
        surgeMultiplier *= 0.95;
        surgeReason = surgeReason ? `${surgeReason} + Early booking discount` : 'Early booking discount';
      }
    }

    // Calculate total with surge
    const subtotal = (baseFare + distanceFare + timeFare) * vehicleMultiplier;
    const totalFare = Math.max(subtotal * surgeMultiplier, FARE_CONFIG.MINIMUM_FARE);

    // Cap at maximum fare
    const finalFare = Math.min(totalFare, FARE_CONFIG.MAXIMUM_FARE);

    return {
      baseFare: Math.round(baseFare * vehicleMultiplier),
      distanceFare: Math.round(distanceFare * vehicleMultiplier),
      timeFare: Math.round(timeFare * vehicleMultiplier),
      surgeMultiplier: Math.round(surgeMultiplier * 100) / 100, // Round to 2 decimal places
      totalFare: Math.round(finalFare),
      estimatedDuration: durationMinutes,
      estimatedDistance: distanceKm,
      surgeReason: surgeReason || undefined,
    };
  }, [serviceType, vehicleType, distanceKm, durationMinutes, scheduledDateTime, currentTime]);
};

src/hooks/usePayment.ts.content:
import { useState } from 'react';
import { RazorpayService, PaymentData, PaymentResult } from '../services/payment/razorpay';
import { useAppStore, useUser } from '../stores/appStore';
import { Alert } from 'react-native';

export const usePayment = () => {
  const user = useUser();
  const { updateBookingStatus } = useAppStore();
  const [isProcessing, setIsProcessing] = useState(false);

  const processPayment = async (
    bookingId: string,
    amount: number,
    description: string
  ): Promise<PaymentResult> => {
    if (!user) {
      const result: PaymentResult = {
        success: false,
        error: 'User not authenticated'
      };
      return result;
    }

    setIsProcessing(true);

    try {
      const paymentData: PaymentData = {
        amount: RazorpayService.formatAmount(amount),
        currency: 'INR',
        bookingId,
        customerId: user.id,
        customerName: user.full_name,
        customerEmail: user.email,
        customerPhone: user.phone || '',
        description: description || 'Cab booking payment',
      };

      const result = await RazorpayService.initiatePayment(paymentData);

      if (result.success) {
        // Update booking status to completed after successful payment
        updateBookingStatus('completed');

        Alert.alert(
          'Payment Successful',
          `Payment of ${RazorpayService.formatDisplayAmount(amount)} completed successfully!`,
          [{ text: 'OK' }]
        );
      } else {
        Alert.alert(
          'Payment Failed',
          result.error || 'Payment could not be processed. Please try again.',
          [{ text: 'Try Again' }]
        );
      }

      return result;
    } catch (error) {
      console.error('Payment processing error:', error);
      const result: PaymentResult = {
        success: false,
        error: 'An unexpected error occurred during payment processing'
      };

      Alert.alert(
        'Payment Error',
        result.error,
        [{ text: 'OK' }]
      );

      return result;
    } finally {
      setIsProcessing(false);
    }
  };

  const getPaymentStatus = async (paymentId: string) => {
    return await RazorpayService.getPaymentStatus(paymentId);
  };

  return {
    processPayment,
    getPaymentStatus,
    isProcessing,
    formatAmount: RazorpayService.formatDisplayAmount,
  };
};

src/hooks/useRealtimeSubscriptions.ts.content:
import { useEffect, useCallback } from 'react';
import { RealtimeService, BookingUpdateCallback, NotificationCallback } from '../services/supabase/realtime';
import { useAppStore, useUser } from '../stores/appStore';
import { Database } from '../services/supabase/client';
import { Alert } from 'react-native';

type Booking = Database['public']['Tables']['bookings']['Row'];
type Notification = Database['public']['Tables']['notifications']['Row'];

export const useRealtimeSubscriptions = () => {
  const user = useUser();
  const { addNotification } = useAppStore();

  // Handle booking status updates
  const handleBookingUpdate = useCallback((booking: Booking) => {
    console.log('Booking status updated:', booking);

    // Show alert for status changes
    const message = RealtimeService.getBookingStatusMessage(booking.status);
    Alert.alert('Booking Update', message);

    // Update local state if needed
    // You can add more logic here to update specific booking in local state
  }, []);

  // Handle new notifications
  const handleNotification = useCallback((notification: Notification) => {
    console.log('New notification received:', notification);

    // Add to local notifications store
    addNotification({
      id: notification.id,
      user_id: notification.user_id,
      title: notification.title,
      message: notification.message,
      type: notification.type,
      is_read: notification.is_read,
      created_at: notification.created_at,
      data: notification.data,
    });

    // Show alert for important notifications
    if (notification.type === 'booking' || notification.type === 'system') {
      Alert.alert(notification.title, notification.message);
    }
  }, [addNotification]);

  // Handle new booking requests (for drivers)
  const handleNewBooking = useCallback((booking: Booking) => {
    console.log('New booking request:', booking);

    // Show alert for new booking
    Alert.alert(
      'New Ride Request',
      `New booking from ${booking.pickup_location?.address || 'Unknown location'}`,
      [
        { text: 'View Details', onPress: () => {
          // Navigate to available rides screen
          // This would need to be implemented based on navigation structure
        }},
        { text: 'Dismiss', style: 'cancel' }
      ]
    );
  }, []);

  // Subscribe to real-time updates when user is authenticated
  useEffect(() => {
    if (!user) return;

    console.log('Setting up real-time subscriptions for user:', user.id);

    // Subscribe to booking updates (only for customer and driver roles)
    let bookingChannel: string | undefined;
    if (user.role === 'customer' || user.role === 'driver') {
      bookingChannel = RealtimeService.subscribeToBookingUpdates(
        user.id,
        user.role,
        handleBookingUpdate
      );
    }

    // Subscribe to notifications
    const notificationChannel = RealtimeService.subscribeToNotifications(
      user.id,
      handleNotification
    );

    // Subscribe to new bookings if user is a driver
    let newBookingChannel: string | undefined;
    if (user.role === 'driver') {
      newBookingChannel = RealtimeService.subscribeToNewBookings(handleNewBooking);
    }

    // Cleanup function
    return () => {
      console.log('Cleaning up real-time subscriptions');
      if (bookingChannel) {
        RealtimeService.unsubscribeFromBookingUpdates(user.id);
      }
      RealtimeService.unsubscribeFromNotifications();
    };
  }, [user, handleBookingUpdate, handleNotification, handleNewBooking]);

  // Return functions for manual subscription management
  return {
    subscribeToBookingUpdates: (userId: string, userRole: 'customer' | 'driver', callback: BookingUpdateCallback) => {
      return RealtimeService.subscribeToBookingUpdates(userId, userRole, callback);
    },

    subscribeToNotifications: (userId: string, callback: NotificationCallback) => {
      return RealtimeService.subscribeToNotifications(userId, callback);
    },

    subscribeToNewBookings: (callback: BookingUpdateCallback) => {
      return RealtimeService.subscribeToNewBookings(callback);
    },

    subscribeToPaymentUpdates: (bookingId: string, callback: (payment: any) => void) => {
      return RealtimeService.subscribeToPaymentUpdates(bookingId, callback);
    },

    unsubscribeAll: () => {
      RealtimeService.unsubscribeAll();
    },

    getBookingStatusMessage: (status: string) => {
      return RealtimeService.getBookingStatusMessage(status as any);
    },

    getNotificationColor: (type: string) => {
      return RealtimeService.getNotificationColor(type);
    },
  };
};

