      icon: 'star',
      iconType: 'MaterialIcons',
    },
    {
      label: 'Member Since',
      value: 'Jan 2024',
      icon: 'calendar-today',
      iconType: 'MaterialIcons',
    },
  ];

  const menuItems = [
    {
      title: 'Payment Methods',
      subtitle: 'Manage cards and UPI',
      icon: 'credit-card',
      iconType: 'MaterialIcons',
      onPress: () => Alert.alert('Coming Soon', 'Payment methods feature coming soon'),
    },
    {
      title: 'Saved Addresses',
      subtitle: 'Manage favorite locations',
      icon: 'location-on',
      iconType: 'MaterialIcons',
      onPress: () => Alert.alert('Coming Soon', 'Saved addresses feature coming soon'),
    },
    {
      title: 'Notifications',
      subtitle: 'Manage app notifications',
      icon: 'notifications',
      iconType: 'MaterialIcons',
      onPress: () => Alert.alert('Coming Soon', 'Notifications settings coming soon'),
    },
    {
      title: 'Help & Support',
      subtitle: 'Get help and contact us',
      icon: 'help',
      iconType: 'MaterialIcons',
      onPress: () => Alert.alert('Coming Soon', 'Help & support feature coming soon'),
    },
    {
      title: 'Privacy Policy',
      subtitle: 'Read our privacy policy',
      icon: 'security',
      iconType: 'MaterialIcons',
      onPress: () => Alert.alert('Coming Soon', 'Privacy policy feature coming soon'),
    },
    {
      title: 'Terms of Service',
      subtitle: 'Read our terms and conditions',
      icon: 'description',
      iconType: 'MaterialIcons',
      onPress: () => Alert.alert('Coming Soon', 'Terms of service feature coming soon'),
    },
  ];

  return (
    <ScrollView style={styles.container}>
      {/* Profile Header */}
      <View style={styles.profileHeader}>
        <View style={styles.profileHeaderContent}>
          <View style={styles.avatar}>
            <Text style={styles.avatarText}>
              {user?.full_name?.charAt(0)?.toUpperCase() || 'U'}
            </Text>
          </View>
          <View style={styles.profileInfo}>
            {isEditing ? (
              <TextInput
                style={styles.nameInput}
                value={editedName}
                onChangeText={setEditedName}
                placeholder="Enter your name"
                autoCapitalize="words"
                placeholderTextColor="#64748b"
              />
            ) : (
              <Text style={styles.nameText}>{user?.full_name || 'Customer'}</Text>
            )}
            <Text style={styles.emailText}>{user?.email}</Text>
            {isEditing ? (
              <TextInput
                style={styles.phoneInput}
                value={editedPhone}
                onChangeText={setEditedPhone}
                placeholder="Enter phone number"
                keyboardType="phone-pad"
                placeholderTextColor="#64748b"
              />
            ) : (
              <Text style={styles.phoneText}>
                {user?.phone || 'No phone number'}
              </Text>
            )}
          </View>
          <TouchableOpacity
            style={styles.editButton}
            onPress={() => {
              if (isEditing) {
                handleSaveProfile();
              } else {
                setIsEditing(true);
              }
            }}
          >
            <Text style={styles.editButtonText}>
              {isEditing ? 'Save' : 'Edit'}
            </Text>
          </TouchableOpacity>
        </View>
      </View>

      {/* Stats */}
      <View style={styles.statsCard}>
        <View style={styles.statsContainer}>
          {profileStats.map((stat, index) => (
            <View key={index} style={styles.statItem}>
              <View style={styles.statIcon}>
                <MaterialIcons name={stat.icon as any} size={28} color="#64748b" />
              </View>
              <Text style={styles.statValue}>{stat.value}</Text>
              <Text style={styles.statLabel}>{stat.label}</Text>
            </View>
          ))}
        </View>
      </View>

      {/* Menu Items */}
      <View style={styles.menuCard}>
        {menuItems.map((item, index) => (
          <TouchableOpacity
            key={index}
