import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  ScrollView,
  RefreshControl,
  Alert,
  StyleSheet,
} from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';

// Import services and stores
import { supabase } from '../../services/supabase/client';
import { useUser } from '../../stores/appStore';

// Import types
import { Booking } from '../../types';

export default function RideHistoryScreen() {
  const user = useUser();
  const [refreshing, setRefreshing] = useState(false);
  const [rides, setRides] = useState<Booking[]>([]);
  const [loading, setLoading] = useState(true);

  const fetchRideHistory = async () => {
    if (!user) return;

    try {
      const { data, error } = await supabase
        .from('bookings')
        .select(`
          *,
          driver:driver_id (
            id,
            full_name
          )
        `)
        .eq('customer_id', user.id)
        .eq('status', 'completed')
        .order('created_at', { ascending: false });

      if (error) {
        console.error('Error fetching ride history:', error);
        Alert.alert('Error', 'Failed to load ride history');
      } else {
        setRides(data || []);
      }
    } catch (error) {
      console.error('Error fetching ride history:', error);
      Alert.alert('Error', 'Failed to load ride history');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRideHistory();
  }, [user]);

  const onRefresh = async () => {
    setRefreshing(true);
    await fetchRideHistory();
    setRefreshing(false);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return '#16a34a';
      case 'cancelled':
        return '#dc2626';
      case 'in_progress':
        return '#ca8a04';
      default:
        return '#6b7280';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'completed':
        return 'Completed';
      case 'cancelled':
        return 'Cancelled';
      case 'in_progress':
        return 'In Progress';
      default:
        return 'Unknown';
    }
  };

  return (
    <ScrollView
      style={styles.container}
      refreshControl={
        <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
      }
    >
      {/* Header */}
      <View style={styles.header}>
        <Text style={styles.headerTitle}>Ride History</Text>
        <Text style={styles.headerSubtitle}>
          {rides.length} {rides.length === 1 ? 'ride' : 'rides'} completed
        </Text>
      </View>

      {/* Ride List */}
      {loading ? (
        <View style={styles.loadingContainer}>
          <Text style={styles.loadingText}>Loading ride history...</Text>
        </View>
      ) : rides.length > 0 ? (
        <View style={styles.rideList}>
          {rides.map((ride) => (
            <TouchableOpacity key={ride.id} style={styles.rideCard}>
              {/* Header */}
              <View style={styles.rideHeader}>
                <View style={styles.rideHeaderLeft}>
                  <Text style={styles.rideDate}>
                    {new Date(ride.created_at).toLocaleDateString()}  {new Date(ride.created_at).toLocaleTimeString()}
                  </Text>
                  <Text style={styles.rideService}>
                    {ride.service_type} Service
                  </Text>
                </View>
                <View
                  style={[styles.statusBadge, { backgroundColor: getStatusColor(ride.status) }]}
                >
                  <Text style={styles.statusText}>
                    {getStatusText(ride.status)}
                  </Text>
                </View>
              </View>

              {/* Route */}
              <View style={styles.routeContainer}>
                <View style={styles.routeStep}>
                  <Text style={styles.routeDot}></Text>
                  <Text style={styles.routeText}>
                    {ride.pickup_location.address || 'Pickup Location'}
                  </Text>
                </View>
                <View style={styles.routeLine} />
                <View style={styles.routeStep}>
                  <Text style={styles.routeDot}></Text>
                  <Text style={styles.routeText}>
                    {ride.drop_location?.address || 'Drop Location'}
                  </Text>
                </View>
              </View>

              {/* Details */}
              <View style={styles.detailsCard}>
                <View style={styles.detailRow}>
                  <Text style={styles.detailLabel}>Vehicle:</Text>
                  <Text style={styles.detailValue}>
                    {ride.vehicle_type}
                  </Text>
                </View>
                <View style={styles.detailRow}>
                  <Text style={styles.detailLabel}>Driver:</Text>
                  <Text style={styles.detailValue}>
                    {(ride as any).driver?.full_name || 'Not assigned'}
                  </Text>
                </View>
                <View style={styles.detailRow}>
                  <Text style={styles.detailLabel}>Fare:</Text>
                  <Text style={styles.detailValue}>
                    {ride.actual_fare || ride.estimated_fare}
                  </Text>
                </View>
              </View>

              {/* Actions */}
              <View style={styles.actionsContainer}>
                <TouchableOpacity
                  style={[styles.actionButton, styles.actionButtonPrimary]}
                  onPress={() => Alert.alert('View Details', 'Detailed ride information coming soon')}
                >
                  <Text style={styles.actionButtonText}>View Details</Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={[styles.actionButton, styles.actionButtonSecondary]}
                  onPress={() => Alert.alert('Book Again', 'Quick booking feature coming soon')}
                >
                  <Text style={[styles.actionButtonText, styles.actionButtonTextSecondary]}>Book Again</Text>
                </TouchableOpacity>
              </View>
            </TouchableOpacity>
          ))}
        </View>
      ) : (
        <View style={styles.emptyContainer}>
          <MaterialIcons name="directions-car" size={60} color="#cbd5e1" />
          <Text style={styles.emptyTitle}>No rides yet</Text>
          <Text style={styles.emptyText}>
            Your completed rides will appear here
          </Text>
        </View>
      )}
    </ScrollView>
  );
}

