import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  ScrollView,
  StyleSheet,
  Alert,
} from 'react-native';
import { MaterialIcons } from '@expo/vector-icons';

// Import services and stores
import { AuthService } from '@/services/supabase/auth';
import { useAppStore, useUser } from '@/stores/appStore';

export default function DriverProfileScreen() {
  const user = useUser();
  const { setLoading } = useAppStore();

  const [isEditing, setIsEditing] = useState(false);
  const [editedName, setEditedName] = useState(user?.full_name || '');
  const [editedPhone, setEditedPhone] = useState(user?.phone || '');

  const handleSaveProfile = async () => {
    if (!editedName.trim()) {
      Alert.alert('Error', 'Name cannot be empty');
      return;
    }

    setLoading(true);
    try {
      const { error } = await AuthService.updateProfile(user!.id, {
        full_name: editedName.trim(),
        phone: editedPhone.trim() || undefined,
      });

      if (error) {
        Alert.alert('Error', 'Failed to update profile');
      } else {
        Alert.alert('Success', 'Profile updated successfully');
        setIsEditing(false);
      }
    } catch (error) {
      Alert.alert('Error', 'An unexpected error occurred');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    Alert.alert(
      'Logout',
      'Are you sure you want to logout?',
      [
        {
          text: 'Cancel',
          style: 'cancel',
        },
        {
          text: 'Logout',
          style: 'destructive',
          onPress: async () => {
            setLoading(true);
            await AuthService.signOut();
            setLoading(false);
          },
        },
      ]
    );
  };

  // Mock driver stats
  const driverStats = [
    {
      label: 'Total Rides',
      value: '1,247',
      icon: 'directions-car',
      iconType: 'MaterialIcons',
    },
    {
      label: 'Rating',
      value: '4.7',
      icon: 'star',
      iconType: 'MaterialIcons',
    },
    {
      label: 'Completion Rate',
      value: '98%',
      icon: 'check-circle',
      iconType: 'MaterialIcons',
    },
    {
      label: 'Member Since',
      value: 'Jan 2024',
      icon: 'calendar-today',
      iconType: 'MaterialIcons',
    },
  ];

  const menuItems = [
    {
      title: 'Vehicle Information',
      subtitle: 'Manage your vehicle details',
      icon: 'airport-shuttle',
      iconType: 'MaterialIcons',
      onPress: () => Alert.alert('Coming Soon', 'Vehicle management coming soon'),
    },
    {
      title: 'Documents',
      subtitle: 'License, insurance, permits',
      icon: 'description',
      iconType: 'MaterialIcons',
      onPress: () => Alert.alert('Coming Soon', 'Document management coming soon'),
    },
    {
      title: 'Bank Details',
      subtitle: 'Update payment information',
      icon: 'account-balance',
      iconType: 'MaterialIcons',
      onPress: () => Alert.alert('Coming Soon', 'Bank details coming soon'),
    },
    {
      title: 'Notifications',
      subtitle: 'Manage app notifications',
      icon: 'notifications',
      iconType: 'MaterialIcons',
      onPress: () => Alert.alert('Coming Soon', 'Notifications settings coming soon'),
    },
    {
      title: 'Support',
      subtitle: 'Get help and contact us',
      icon: 'help',
      iconType: 'MaterialIcons',
      onPress: () => Alert.alert('Coming Soon', 'Support coming soon'),
    },
    {
      title: 'Settings',
      subtitle: 'App preferences and privacy',
      icon: 'settings',
      iconType: 'MaterialIcons',
      onPress: () => Alert.alert('Coming Soon', 'Settings coming soon'),
    },
  ];

  return (
    <ScrollView style={styles.container}>
      {/* Profile Header */}
      <View style={styles.header}>
        <View style={styles.avatarContainer}>
          <Text style={styles.avatarText}>
            {user?.full_name?.charAt(0)?.toUpperCase() || 'D'}
          </Text>
        </View>
        <View style={styles.userInfo}>
          {isEditing ? (
            <TextInput
              style={styles.nameInput}
              value={editedName}
              onChangeText={setEditedName}
              placeholder="Enter your name"
              autoCapitalize="words"
              placeholderTextColor="#64748b"
            />
          ) : (
            <Text style={styles.userName}>{user?.full_name || 'Driver'}</Text>
          )}
          <Text style={styles.userEmail}>{user?.email}</Text>
          {isEditing ? (
            <TextInput
              style={styles.phoneInput}
              value={editedPhone}
              onChangeText={setEditedPhone}
              placeholder="Enter phone number"
              keyboardType="phone-pad"
              placeholderTextColor="#64748b"
            />
          ) : (
            <Text style={styles.userPhone}>
              {user?.phone || 'No phone number'}
            </Text>
          )}
        </View>
        <TouchableOpacity
          style={styles.editButton}
          onPress={() => {
            if (isEditing) {
              handleSaveProfile();
            } else {
              setIsEditing(true);
            }
          }}
        >
          <Text style={styles.editButtonText}>
            {isEditing ? 'Save' : 'Edit'}
          </Text>
        </TouchableOpacity>
      </View>

      {/* Driver Stats */}
      <View style={styles.statsContainer}>
        <View style={{ flexDirection: 'row', justifyContent: 'space-around' }}>
          {driverStats.map((stat, index) => (
            <View key={index} style={styles.statItem}>
              <View style={styles.statIcon}>
                <MaterialIcons name={stat.icon as any} size={24} color="#64748b" />
              </View>
              <Text style={styles.statValue}>{stat.value}</Text>
              <Text style={styles.statLabel}>{stat.label}</Text>
            </View>
          ))}
        </View>
      </View>

      {/* Menu Items */}
      <View style={styles.menuContainer}>
        {menuItems.map((item, index) => (
          <TouchableOpacity
            key={index}
            style={styles.menuItem}
            onPress={item.onPress}
          >
            <View style={styles.menuItemLeft}>
              <View style={styles.menuItemIcon}>
                <MaterialIcons name={item.icon as any} size={20} color="#64748b" />
              </View>
              <View style={styles.menuItemText}>
                <Text style={styles.menuItemTitle}>{item.title}</Text>
                <Text style={styles.menuItemSubtitle}>{item.subtitle}</Text>
              </View>
            </View>
            <Text style={styles.menuItemArrow}></Text>
          </TouchableOpacity>
        ))}
      </View>

      {/* Logout Button */}
      <View style={styles.logoutContainer}>
        <TouchableOpacity
          style={styles.logoutButton}
          onPress={handleLogout}
        >
          <Text style={styles.logoutButtonText}>Logout</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
}

