import RazorpayCheckout from 'react-native-razorpay';
import { supabase } from '../supabase/client';
import { RAZORPAY_KEY_ID } from '../../constants';

export interface PaymentData {
  amount: number;
  currency: string;
  bookingId: string;
  customerId: string;
  customerName: string;
  customerEmail: string;
  customerPhone: string;
  description: string;
}

export interface PaymentResult {
  success: boolean;
  paymentId?: string;
  orderId?: string;
  error?: string;
}

export class RazorpayService {
  /**
   * Initialize payment with Razorpay
   */
  static async initiatePayment(paymentData: PaymentData): Promise<PaymentResult> {
    try {
      // Create order in Supabase (this would call the edge function)
      const { data: orderData, error: orderError } = await supabase.functions.invoke('create-razorpay-order', {
        body: {
          amount: paymentData.amount,
          currency: paymentData.currency,
          booking_id: paymentData.bookingId,
          customer_id: paymentData.customerId,
        }
      });

      if (orderError) {
        console.error('Error creating Razorpay order:', orderError);
        return {
          success: false,
          error: 'Failed to create payment order'
        };
      }

      const orderId = orderData.order_id;

      // Razorpay checkout options
      const options = {
        key: RAZORPAY_KEY_ID,
        amount: paymentData.amount,
        currency: paymentData.currency,
        name: 'SDM Cab Hailing',
        description: paymentData.description,
        order_id: orderId,
        prefill: {
          name: paymentData.customerName,
          email: paymentData.customerEmail,
          contact: paymentData.customerPhone,
        },
        theme: {
          color: '#3b82f6',
        },
      };

      // Open Razorpay checkout
      const paymentResponse = await RazorpayCheckout.open(options);

      // Verify payment
      const verificationResult = await this.verifyPayment({
        paymentId: paymentResponse.razorpay_payment_id,
        orderId: paymentResponse.razorpay_order_id,
        signature: paymentResponse.razorpay_signature,
        bookingId: paymentData.bookingId,
      });

      if (verificationResult.success) {
        return {
          success: true,
          paymentId: paymentResponse.razorpay_payment_id,
          orderId: paymentResponse.razorpay_order_id,
        };
      } else {
        return {
          success: false,
          error: 'Payment verification failed'
        };
      }

    } catch (error: any) {
      console.error('Razorpay payment error:', error);

      // Handle specific Razorpay errors
      if (error.code === 0) {
        // Payment cancelled by user
        return {
          success: false,
          error: 'Payment cancelled'
        };
      } else if (error.code === 1) {
        // Payment failed
        return {
          success: false,
          error: 'Payment failed'
        };
      } else {
        return {
          success: false,
          error: error.description || 'Payment failed'
        };
      }
    }
  }

  /**
   * Verify payment with backend
   */
  static async verifyPayment(verificationData: {
    paymentId: string;
    orderId: string;
    signature: string;
    bookingId: string;
  }): Promise<PaymentResult> {
    try {
      const { data, error } = await supabase.functions.invoke('verify-razorpay-payment', {
        body: {
          razorpay_payment_id: verificationData.paymentId,
          razorpay_order_id: verificationData.orderId,
          razorpay_signature: verificationData.signature,
          booking_id: verificationData.bookingId,
        }
      });

      if (error) {
        console.error('Payment verification error:', error);
        return {
          success: false,
          error: 'Payment verification failed'
        };
      }

      return {
        success: true,
        paymentId: verificationData.paymentId,
        orderId: verificationData.orderId,
      };

    } catch (error) {
      console.error('Payment verification error:', error);
      return {
        success: false,
        error: 'Payment verification failed'
      };
    }
  }

  /**
   * Get payment status
   */
  static async getPaymentStatus(paymentId: string): Promise<any> {
    try {
      const { data, error } = await supabase
        .from('payments')
        .select('*')
        .eq('razorpay_payment_id', paymentId)
        .single();

      if (error) {
        console.error('Error fetching payment status:', error);
        return null;
      }

      return data;
    } catch (error) {
      console.error('Error fetching payment status:', error);
      return null;
    }
  }

  /**
   * Format amount for Razorpay (paise)
   */
  static formatAmount(amount: number): number {
    return Math.round(amount * 100); // Convert to paise
  }

  /**
   * Format amount for display (rupees)
   */
  static formatDisplayAmount(amount: number): string {
    return `${amount.toFixed(2)}`;
  }

  /**
   * Get payment method name
   */
  static getPaymentMethodName(method: string): string {
    switch (method.toLowerCase()) {
      case 'card':
        return 'Credit/Debit Card';
      case 'netbanking':
        return 'Net Banking';
      case 'wallet':
        return 'Wallet';
      case 'upi':
        return 'UPI';
      default:
        return method;
    }
  }
}

src/services/supabase/auth.ts.content:
import { supabase } from './client';
import { User, Customer, Driver } from '../../types';
import { useAppStore } from '../../stores/appStore';

export class AuthService {
  // Sign up with email and password
  static async signUp(
    email: string,
    password: string,
    fullName: string,
    phone?: string,
    role: 'customer' | 'driver' = 'customer'
  ) {
    try {
      const { data, error } = await supabase.auth.signUp({
        email,
        password,
        options: {
          data: {
            full_name: fullName,
            phone: phone || '',
            role: role,
          },
        },
      });

      if (error) throw error;

      // Create user profile in users table
      if (data.user) {
        const { error: profileError } = await supabase
          .from('users')
          .insert({
            id: data.user.id,
            email: data.user.email!,
            phone: phone || null,
            full_name: fullName,
            role: role,
          });

        if (profileError) throw profileError;
      }

      return { data, error: null };
    } catch (error) {
      return { data: null, error: error as Error };
    }
  }

  // Sign in with email and password
  static async signIn(email: string, password: string) {
    try {
      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (error) throw error;

      // Fetch user profile
      if (data.user) {
        const profile = await this.getUserProfile(data.user.id);
        if (profile) {
          useAppStore.getState().setUser(profile);
          useAppStore.getState().setAuthenticated(true);
        }
      }

      return { data, error: null };
    } catch (error) {
      return { data: null, error: error as Error };
    }
  }

  // Sign out
  static async signOut() {
    try {
      const { error } = await supabase.auth.signOut();
      if (error) throw error;

      useAppStore.getState().logout();
      return { error: null };
    } catch (error) {
      return { error: error as Error };
    }
  }

  // Get current user profile
  static async getUserProfile(userId: string): Promise<User | null> {
    try {
      const { data, error } = await supabase
        .from('users')
        .select('*')
        .eq('id', userId)
        .single();

      if (error) throw error;

      return data as User;
    } catch (error) {
      console.error('Error fetching user profile:', error);
      return null;
    }
  }

  // Update user profile
  static async updateProfile(userId: string, updates: Partial<User>) {
    try {
      const { data, error } = await supabase
        .from('users')
        .update({
          ...updates,
          updated_at: new Date().toISOString(),
        })
        .eq('id', userId)
        .select()
        .single();

      if (error) throw error;

      // Update local state
      useAppStore.getState().setUser(data as User);

      return { data, error: null };
    } catch (error) {
      return { data: null, error: error as Error };
    }
  }

  // Reset password
  static async resetPassword(email: string) {
    try {
      const { error } = await supabase.auth.resetPasswordForEmail(email, {
        redirectTo: 'sdmcabhailing://reset-password',
      });

      if (error) throw error;

      return { error: null };
    } catch (error) {
      return { error: error as Error };
    }
  }

  // Update password
  static async updatePassword(newPassword: string) {
    try {
      const { error } = await supabase.auth.updateUser({
        password: newPassword,
      });

      if (error) throw error;

      return { error: null };
    } catch (error) {
      return { error: error as Error };
    }
  }

  // Get current session
  static async getCurrentSession() {
    try {
      const { data, error } = await supabase.auth.getSession();

      if (error) throw error;

      return { session: data.session, error: null };
    } catch (error) {
      return { session: null, error: error as Error };
    }
  }

  // Refresh session
  static async refreshSession() {
    try {
      const { data, error } = await supabase.auth.refreshSession();

      if (error) throw error;

      return { session: data.session, error: null };
    } catch (error) {
      return { session: null, error: error as Error };
    }
  }

  // Check if user is authenticated
  static async isAuthenticated(): Promise<boolean> {
    try {
      const { session } = await this.getCurrentSession();
      return !!session;
    } catch {
      return false;
    }
  }

  // Initialize auth state listener
  static initializeAuthListener() {
    supabase.auth.onAuthStateChange(async (event, session) => {
      console.log('Auth state changed:', event, session?.user?.id);

      if (event === 'SIGNED_IN' && session?.user) {
        const profile = await this.getUserProfile(session.user.id);
        if (profile) {
          useAppStore.getState().setUser(profile);
          useAppStore.getState().setAuthenticated(true);
        }
      } else if (event === 'SIGNED_OUT') {
        useAppStore.getState().logout();
      }
    });
  }

  // Validate email format
  static isValidEmail(email: string): boolean {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  }

  // Validate phone format (Indian phone numbers)
  static isValidPhone(phone: string): boolean {
    const phoneRegex = /^[6-9]\d{9}$/;
    return phoneRegex.test(phone);
  }

  // Validate password strength
  static isValidPassword(password: string): boolean {
    return password.length >= 8;
  }
}

